const ld = require('lodash');
let responseData;
const contentType = pm.response.headers.get('Content-Type');

if(contentType === "application/xml"){
    var parseString = require('xml2js').parseString;
    parseString(pm.response.body, function (err,result) {
      if (err) {
        console.error('Error parsing XML:', err);
      } else {
        responseData = result;  
      }
    });
} else{
    responseData = pm.response.json();
}

if (pm.variables.get("statusCode")) {
    pm.expect(parseInt(pm.variables.get("statusCode"))).to.be.equal(pm.response.code);
}

if (pm.response.code == 201){
    pm.test("Created", function () {
        pm.expect(pm.response.status).to.be.eql("Created");
        pm.test('Response has the `orderId` field',function(){
            pm.expect(ld.get(pm.response.json(), `orderId`, null)).to.exist;
            pm.expect(typeof ld.get(pm.response.json(), `orderId`, null)).to.equal("string");
        })

        pm.test('Response has the `requestId` field',function(){
            pm.expect(ld.get(pm.response.json(), `requestId`, null)).to.exist;
            pm.expect(typeof ld.get(pm.response.json(), `requestId`, null)).to.equal("string");
        })

    });

}

// ... similar tests for other response codes ...

if (pm.response.code == 500){
    pm.test("Internal Server Error", function () {
        pm.expect(pm.response.status).to.be.eql("Internal Server Error");
        pm.test('Response has the `code` field',function(){
            pm.expect(ld.get(pm.response.json(), `code`, null)).to.exist;
            pm.expect(typeof ld.get(pm.response.json(), `code`, null)).to.equal("string");
        })

        pm.test('Response has the `message` field',function(){
            pm.expect(ld.get(pm.response.json(), `message`, null)).to.exist;
            pm.expect(typeof ld.get(pm.response.json(), `message`, null)).to.equal("string");
        })

        pm.test('Response has the `status` field',function(){
            pm.expect(ld.get(pm.response.json(), `status`, null)).to.exist;
            pm.expect(typeof ld.get(pm.response.json(), `status`, null)).to.equal("string");
        })

    });

}
