describe('GET /products', function () {
  it('respond with json containing a list of all products', function (done) {
    request(app)
      .get('/products')
      .set('Accept', 'application/json')
      .expect('Content-Type', /json/)
      .expect(200, done);
  });
});

//--------------new_test_case-----------
describe('POST /products', function () {
  it('responds with json containing the created product', function (done) {
    const productData = {
      name: 'Test Product',
      price: '19.99'
    };

    request(app)
      .post('/products')
      .send(productData)
      .set('Accept', 'application/json')
      .expect('Content-Type', /json/)
      .expect(201)
      .end((err, res) => {
        if (err) return done(err);
        expect(res.body).to.include({
          name: 'Test Product',
          price: '19.99'
        });
        done();
      });
  });
});

