const assert = require('chai').assert;
const myFunc = require('../src/myFunc.js');

//Existing Test Case
describe('myFunc()', function () {
  it('should return “Hello, World!” when no parameters are provided', function() {
    assert.equal(myFunc(), 'Hello, World!');
  });
});

//Updated Test Case
describe('myFunc(text)', function () {
  it('should return text when one parameter is provided', function() {
    let text = 'Artillery Test';
    assert.equal(myFunc(text), 'Artillery Test');
  });

  it('should return “Hello, Artillery!” when the parameter is "Artillery"', function() {
    assert.equal(myFunc('Artillery'), 'Hello, Artillery!');
  });

  it('should return responded error message for null parameter', function() {
    assert.throws(() => { myFunc(null); }, Error, 'Invalid input');
  });
});

